            linktext = t[start+2:end-2]
            if '|' in linktext:
                linktext = linktext.split('|')
                label = linktext[1]
                title = linktext[0]
                url = urlBegin + title

            else:

                label = linktext
                title = linktext
                url = urlBegin+linktext


            text+=t[lastEnd:start]+label
            #text+=t[end:nextStart]
            lastEnd = end
            link['start'] = len(text)- len(label)
            link['end'] = len(text)
